$.fn.dataTableExt.afnFiltering.push(
	function( oSettings, aData, iDataIndex ) {
		var iFini = moment(document.getElementById('fini').value, 'MM-YYYY').format('01-MM-YYYY');
		var iFfin = moment(document.getElementById('ffin').value, 'MM-YYYY').format('01-MM-YYYY');
		var iStartDateCol = 16;
		var iEndDateCol = 16;

		iFini=iFini.substring(6,10) + iFini.substring(3,5)+ iFini.substring(0,2);
		iFfin=iFfin.substring(6,10) + iFfin.substring(3,5)+ iFfin.substring(0,2);

		let startingDate = moment(aData[iStartDateCol], 'MM-YYYY').format('01-MM-YYYY');
		let EndingDate = moment(aData[iEndDateCol], 'MM-YYYY').format('01-MM-YYYY');

		var datofini=startingDate.substring(6,10) + startingDate.substring(3,5)+ startingDate.substring(0,2);
		var datoffin=EndingDate.substring(6,10) + EndingDate.substring(3,5)+ EndingDate.substring(0,2);

		if ( iFini === "" && iFfin === "" )
		{
			return true;
		}
		else if ( iFini <= datofini && iFfin === "")
		{
			return true;
		}
		else if ( iFfin >= datoffin && iFini === "")
		{
			return true;
		}
		else if (iFini <= datofini && iFfin >= datoffin)
		{
			return true;
		}
		return false;
	}
);
